};
  return (
    <div className="container">
      <h1>Task Manager</h1>
      {/* <Home /> This will render the Home component */}
      <div className="task-form">
        <TaskForm onSubmit={editTask ? updateTask : addTask} />
      </div>
      <TaskList
        tasks={tasks}
        onDelete={deleteTask}
        onEdit={editTaskHandler}
      />
    </div>
  );
};

export default Home;
