import React, { useState } from "react";

const TaskForm = ({ onSubmit, tasks }) => {
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState("Personal");
  const [categoryFilter, setCategoryFilter] = useState("All"); // Initialize category filter state

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim()) return;
    onSubmit({ title, category });
    setTitle("");
  };

  const handleCategoryFilterChange = (e) => {
    setCategoryFilter(e.target.value);
  };

  // Filter tasks based on category filter
  const filteredTasks = categoryFilter === "All" ? tasks : tasks.filter(task => task.category === categoryFilter);

  return (
    <div>
      <form className="form-container" onSubmit={handleSubmit}>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="Personal">Personal</option>
          <option value="Work">Work</option>
          <option value="Study">Study</option>
        </select>
        <button type="submit">Add Task</button>
      </form>
      <div>
        <label>Filter by category:</label>
        <select value={categoryFilter} onChange={handleCategoryFilterChange}>
          <option value="All">All</option>
          <option value="Personal">Personal</option>
          <option value="Work">Work</option>
          <option value="Study">Study</option>
        </select>
      </div>
      <div>
        <ul>
          {filteredTasks.map((task, index) => (
            <li key={index}>{task.title} - {task.category}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TaskForm;
